(()=>{function i(t){let a=0;t.directive("page",n=>{let e=t.$data(n),o="scope-"+Date.now()+"-"+a;if(a++,!e||typeof e.page!="function")throw new Error("Alpine data must have a 'page' function");typeof e.styles=="function"&&t.effect(()=>{n.innerHTML+=`
        <style>
          [data-scope="${o}"] {
            ${e.styles()}
          }
        </style>
        `}),t.effect(()=>{n.innerHTML+=`
      <div data-scope="${o}">
        ${e.page()}
      </div>
      `})})}document.addEventListener("alpine:init",()=>window.Alpine.plugin(i));})();
