(()=>{function s(t){let a=0;t.directive("page",n=>{let e=t.$data(n),o="scope-"+Date.now()+"-"+a;if(a++,!e||typeof e.page!="function")throw new Error("Alpine data must have a 'page' function");typeof e.styles=="function"&&t.effect(()=>{let i=e.styles().replace(/(^|\})\s*([^{\s]+)\s*{/g,(p,c,r)=>`${c} [data-scope="${o}"] ${r} {`);n.innerHTML+=`
        <style>
          ${i}
        </style>
        `}),t.effect(()=>{n.innerHTML+=`
      <div data-scope="${o}">
        ${e.page()}
      </div>
      `})})}document.addEventListener("alpine:init",()=>window.Alpine.plugin(s));})();
